pragma solidity 0.8.9;

library Math {
    function mul(
        uint256 a,
        uint256 b,
        string memory overflowError
    ) internal pure returns (uint256 c) {
        require(b == 0 || a == 0 || ((c = a * b) / b) == a, overflowError);
    }

    function add(
        uint256 a,
        uint256 b,
        string memory overflowError
    ) internal pure returns (uint256 c) {
        require((c = a + b) >= a, overflowError);
    }

    function sub(
        uint256 a,
        uint256 b,
        string memory underflowError
    ) internal pure returns (uint256 c) {
        require((c = a - b) <= a, underflowError);
    }

    function mul18(
        uint256 a18,
        uint256 b18,
        string memory overflowError
    ) internal pure returns (uint256) {
        return mul(a18, b18, overflowError) / 10**18;
    }

    function div18(
        uint256 a18,
        uint256 b18,
        string memory overflowError
    ) internal pure returns (uint256) {
        return mul(a18, 10**18, overflowError) / b18;
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a <= b ? a : b;
    }

    function max(uint256 a, uint256 b) internal pure returns (uint256) {
        return a >= b ? a : b;
    }

    function smin(int256 a, int256 b) internal pure returns (int256) {
        return a <= b ? a : b;
    }

    function smax(int256 a, int256 b) internal pure returns (int256) {
        return a >= b ? a : b;
    }

    function sqrt(uint256 a) internal pure returns (uint256 c) {
        uint256 tmp = (a + 1) / 2;
        c = a;
        while (tmp < c) {
            c = tmp;
            tmp = ((a / tmp) + tmp) / 2;
        }
    }
}
","keccak256":"0x8302e97d326d0408b331422faebaf19a91102b2a14a395811380417c96d54a57","license":"AGPL-1.0"},"src/OuterSpace.sol":{"content":"// SPDX-License-Identifier: AGPL-1.0

pragma solidity 0.8.9;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Libraries/Extraction.sol";
import "./Libraries/Math.sol";
import "hardhat-deploy/solc_0.8/proxy/Proxied.sol";
import "./Interfaces/IAlliance.sol";
import "./AllianceRegistry.sol";

contract OuterSpace is Proxied {
    using Extraction for bytes32;

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // CONSTANTS
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------

    uint256 internal constant DECIMALS_18 = 1e18;
    uint32 internal constant ACTIVE_MASK = 2**31;
    int256 internal constant UINT32_MAX = 2**32 - 1;

    int256 internal constant EXPANSION = 8;
    uint32 internal constant INITIAL_SPACE = 16;
    uint256 internal constant GIFT_TAX_PER_10000 = 2500;

    uint256 internal constant COMBAT_RULE_SWITCH_TIME = 1620144000; // Tuesday, 4 May 2021 16:00:00 GMT

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // CONFIGURATION / IMMUTABLE
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------

    AllianceRegistry public immutable allianceRegistry;
    bytes32 internal immutable _genesis;
    IERC20 internal immutable _stakingToken;
    uint256 internal immutable _resolveWindow;
    uint256 internal immutable _timePerDistance;
    uint256 internal immutable _exitDuration;
    uint32 internal immutable _acquireNumSpaceships;
    uint32 internal immutable _productionSpeedUp;
    uint256 internal immutable _frontrunningDelay;
    uint256 internal immutable _productionCapAsDuration;
    uint256 internal immutable _fleetSizeFactor6;

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // STORAGE
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------

    mapping(uint256 => Planet) internal _planets;
    mapping(uint256 => Fleet) internal _fleets;

    // struct AccountData {
    //     uint128 stakeReadyToBeWithdrawn;
    //     uint8 numAlliances;
    // }
    // mapping(address => AccountData) internal _accounts;
    mapping(address => uint256) internal _stakeReadyToBeWithdrawn;

    mapping(address => mapping(address => bool)) internal _operators;

    // mapping(address => mapping(IAlliance => uint256)) internal _alliances;

    // front running protection : _frontruunningDelay / 2 slots
    struct InFlight {
        uint64 flying;
        uint64 destroyed;
    }
    // TODO make it namespaces per user, currently it is possible (though unlikely) for 2 users to share a slot if one attack another and quickly send away spaceships
    mapping(uint256 => mapping(uint256 => InFlight)) internal _inFlight;

    struct Discovered {
        uint32 minX;
        uint32 maxX;
        uint32 minY;
        uint32 maxY;
    }

    Discovered internal _discovered;

    struct Planet {
        address owner;
        uint32 exitTime; // could be used as startTime with first bit telling whether it is exit or startTime => means exiting would produce spacehips / or not, but not based on startTime
        uint32 numSpaceships; // uint31 + first bit => active
        uint32 lastUpdated; // also used as native-destruction indicator
    }

    struct Fleet {
        address owner;
        uint32 launchTime;
        uint32 quantity;
        // TODO uint32 delay
    }

    // rewards
    mapping(address => uint256) internal _prevRewardIds;
    mapping(uint256 => uint256) internal _rewards;
    mapping(address => mapping(uint256 => bool)) internal _rewardsToWithdraw;

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // EVENTS
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------

    event PlanetStake(address indexed acquirer, uint256 indexed location, uint32 numSpaceships, uint256 stake);
    event FleetSent(
        address indexed fleetSender,
        address indexed fleetOwner,
        uint256 indexed from,
        address operator,
        uint256 fleet,
        uint32 quantity,
        uint32 newNumSpaceships
    );

    // TODO add fromPlanet to the event ?
    event FleetArrived(
        uint256 indexed fleet,
        address indexed fleetOwner,
        address indexed destinationOwner,
        uint256 destination,
        bool gift,
        uint32 fleetLoss,
        uint32 planetLoss,
        uint32 inFlightFleetLoss,
        uint32 inFlightPlanetLoss,
        bool won,
        uint32 newNumspaceships
    );

    // event AllianceLink(IAlliance indexed alliance, address indexed player, bool joining);

    event PlanetReset(uint256 indexed location);

    event PlanetExit(address indexed owner, uint256 indexed location);

    event ExitComplete(address indexed owner, uint256 indexed location, uint256 stake);

    event RewardSetup(uint256 indexed location, address indexed giver, uint256 rewardId);
    event RewardToWithdraw(address indexed owner, uint256 indexed location, uint256 indexed rewardId);

    event StakeToWithdraw(address indexed owner, uint256 newStake);

    event ApprovalForAll(address indexed owner, address indexed operator, bool approved);

    event Initialized(uint32 minX, uint32 maxX, uint32 minY, uint32 maxY, bytes32 genesis);

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // CONSTRUCTOR / INITIALIZATION
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------

    constructor(
        IERC20 stakingToken,
        AllianceRegistry theAllianceRegistry,
        bytes32 genesis,
        uint32 resolveWindow,
        uint32 timePerDistance,
        uint32 exitDuration,
        uint32 acquireNumSpaceships,
        uint32 productionSpeedUp,
        uint32 frontrunningDelay,
        uint32 productionCapAsDuration,
        uint32 fleetSizeFactor6
    ) {
        uint32 t = timePerDistance / 4; // the coordinates space is 4 times bigger
        require(t * 4 == timePerDistance, "TIME_PER_DIST_NOT_DIVISIBLE_4");

        _stakingToken = stakingToken;
        allianceRegistry = theAllianceRegistry;
        _genesis = genesis;
        _resolveWindow = resolveWindow;
        _timePerDistance = t;
        _exitDuration = exitDuration;
        _acquireNumSpaceships = acquireNumSpaceships;
        _productionSpeedUp = productionSpeedUp;
        _frontrunningDelay = frontrunningDelay;
        _productionCapAsDuration = productionCapAsDuration;
        _fleetSizeFactor6 = fleetSizeFactor6;

        postUpgrade(
            stakingToken,
            theAllianceRegistry,
            genesis,
            resolveWindow,
            timePerDistance,
            exitDuration,
            acquireNumSpaceships,
            productionSpeedUp,
            frontrunningDelay,
            productionCapAsDuration,
            fleetSizeFactor6
        );
    }

    function postUpgrade(
        IERC20,
        AllianceRegistry,
        bytes32 genesis,
        uint32,
        uint32,
        uint32,
        uint32,
        uint32,
        uint32,
        uint32,
        uint32
    ) public proxied {
        if (_discovered.minX == 0) {
            _discovered = Discovered({
                minX: INITIAL_SPACE,
                maxX: INITIAL_SPACE,
                minY: INITIAL_SPACE,
                maxY: INITIAL_SPACE
            });
            emit Initialized(_discovered.minX, _discovered.maxX, _discovered.minY, _discovered.maxY, genesis);
        }
    }


    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // STAKING / PRODUCTION CAPTURE
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------

    function onTokenTransfer(
        address,
        uint256 amount,
        bytes calldata data
    ) public returns (bool) {
        require(msg.sender == address(_stakingToken), "INVALID_ERC20");
        (address acquirer, uint256 location) = abi.decode(data, (address, uint256));
        _acquire(acquirer, amount, location); // we do not care of who the payer is
        return true;
    }

    function onTokenPaidFor(
        address,
        address forAddress,
        uint256 amount,
        bytes calldata data
    ) external returns (bool) {
        require(msg.sender == address(_stakingToken), "INVALID_ERC20");
        uint256 location = abi.decode(data, (uint256));
        _acquire(forAddress, amount, location); // we do not care of who the payer is
        return true;
    }

    function acquireViaTransferFrom(uint256 location, uint256 amount) public {
        address sender = _msgSender();
        _acquire(sender, amount, location);
        _stakingToken.transferFrom(sender, address(this), amount);
    }

    function resetPlanet(uint256 location) external onlyProxyAdmin {
        _planets[location].owner = address(0);
        _planets[location].exitTime = 0;
        _planets[location].numSpaceships = 0;
        _planets[location].lastUpdated = 0;
        emit PlanetReset(location);
    }

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // REWARD SETUP
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // TODO : ERC20, ERC721, ERC1155
    // remove sponsor, use msg.sender and this could be special contracts
    // TODO : reenable, removed because of code size issue
    function addReward(uint256 location, address sponsor) external onlyProxyAdmin {
        Planet memory planet = _planets[location];
        if (_hasJustExited(planet.exitTime)) {
            _setPlanetAfterExit(location, planet.owner, _planets[location], address(0), 0);
        }

        uint256 rewardId = _rewards[location];
        if (rewardId == 0) {
            rewardId = ++_prevRewardIds[sponsor];
            _rewards[location] = (uint256(uint160(sponsor)) << 96) + rewardId;
        }
        // TODO should it fails if different sponsor added reward before

        // TODO rewardId association with the actual rewards // probably contract address holding the reward
        emit RewardSetup(location, sponsor, rewardId);
    }

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // EXIT / WITHDRAWALS
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------

    function exitFor(address owner, uint256 location) external {
        Planet storage planet = _getPlanet(location);
        require(owner == planet.owner, "NOT_OWNER");
        require(planet.exitTime == 0, "EXITING_ALREADY"); // if you own the planet again, you ll need to first withdraw
        planet.exitTime = uint32(block.timestamp);
        emit PlanetExit(owner, location);
    }

    function fetchAndWithdrawFor(address owner, uint256[] calldata locations) external {
        uint256 addedStake = 0;
        for (uint256 i = 0; i < locations.length; i++) {
            Planet storage planet = _getPlanet(locations[i]);
            if (_hasJustExited(planet.exitTime)) {
                require(owner == planet.owner, "NOT_OWNER");
                addedStake += _setPlanetAfterExitWithoutUpdatingStake(locations[i], owner, planet, address(0), 0); // no need of event as exitTime passed basically mean owner zero and spaceships zero
            }
        }
        uint256 newStake = _stakeReadyToBeWithdrawn[owner] + addedStake;
        _withdrawAll(owner, newStake);
    }

    function balanceToWithdraw(address owner) external view returns (uint256) {
        return _stakeReadyToBeWithdrawn[owner];
    }

    function withdrawFor(address owner) external {
        uint256 amount = _stakeReadyToBeWithdrawn[owner];
        _withdrawAll(owner, amount);
    }

    function _withdrawAll(address owner, uint256 amount) internal {
        _updateStake(owner, 0);
        require(_stakingToken.transfer(owner, amount), "FAILED_TRANSFER"); // TODO FundManager
    }

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // FLEET RESOLUTION, ATTACK / REINFORCEMENT
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------

    struct FleetResolution {
        uint256 from;
        uint256 to;
        uint256 distance;
        bool gift;
        address specific;
        bytes32 secret;
        address fleetSender;
        address operator;
    }

    function resolveFleet(uint256 fleetId, FleetResolution calldata resolution) external {
         require(
            uint256(keccak256(
                abi.encodePacked(
                    keccak256(abi.encodePacked(resolution.secret, resolution.to, resolution.gift, resolution.specific)),
                    resolution.from, resolution.fleetSender, resolution.operator
                )
            )) == fleetId,
            "INVALID_FLEET_DATA_OR_SECRET"
        );
        _resolveFleet(fleetId, resolution);
    }

    // --------------------------------------------------------------------------------------------------------------------------------------------------------------
    // FLEET SENDING
    // --------------------------------------------------------------------------------------------------------------------------------------------------------------


    struct FleetLaunch {
        address fleetSender;
        address fleetOwner;
        uint256 from;
        uint32 quantity;
        bytes32 toHash;
    }

    function send(
        uint256 from,
        uint32 quantity,
        bytes32 toHash
    ) external {
        address sender = _msgSender();
        uint256 fleetId = uint256(keccak256(abi.encodePacked(toHash, from, sender, sender)));
        _sendFor(fleetId, sender, FleetLaunch({
            fleetSender: sender,
            fleetOwner: sender,
            from: from,
            quantity: quantity,
            toHash: toHash
        }));
    }

    function sendFor(FleetLaunch calldata launch) external { //  bytes calldata fleetSignature // TODO for fleetOwner's signature ?

        address operator = _msgSender();
        if (operator != launch.fleetSender) {
            require(_operators[launch.fleetSender][operator], "NOT_AUTHORIZED_TO_SEND");
        }
        uint256 fleetId = uint256(keccak256(abi.encodePacked(launch.toHash, launch.from, launch.fleetSender, operator)));

        if (launch.fleetOwner != launch.fleetSender && launch.fleetOwner != operator) {
            // TODO use signature from fleetOwner instead?
            require(_operators[launch.fleetOwner][operator], "NOT_AUTHORIZED_TO_FLEET");
        }

        _sendFor(fleetId, operator, launch);
    }

