// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.9;

import "../base/ImportingOuterSpaceConstants.sol";
import "../events/ImportingOuterSpaceEvents.sol";
import "../base/UsingOuterSpaceDataLayout.sol";

contract OuterSpaceInitializationFacet is
    ImportingOuterSpaceConstants,
    ImportingOuterSpaceEvents,
    UsingOuterSpaceDataLayout
{
    bytes32 internal immutable _genesis;
    uint256 internal immutable _resolveWindow;
    uint256 internal immutable _timePerDistance;
    uint256 internal immutable _exitDuration;
    uint32 internal immutable _acquireNumSpaceships;
    uint32 internal immutable _productionSpeedUp;
    uint256 internal immutable _frontrunningDelay;
    uint256 internal immutable _productionCapAsDuration;
    uint256 internal immutable _upkeepProductionDecreaseRatePer10000th;
    uint256 internal immutable _fleetSizeFactor6;
    uint32 internal immutable _expansionDelta; // = 8;
    uint32 internal immutable _initialSpaceExpansion; // = 16;
    uint256 internal immutable _giftTaxPer10000; // = 2500;

    struct Config {
        bytes32 genesis;
        uint256 resolveWindow;
        uint256 timePerDistance;
        uint256 exitDuration;
        uint32 acquireNumSpaceships;
        uint32 productionSpeedUp;
        uint256 frontrunningDelay;
        uint256 productionCapAsDuration;
        uint256 upkeepProductionDecreaseRatePer10000th;
        uint256 fleetSizeFactor6;
        uint32 initialSpaceExpansion;
        uint32 expansionDelta;
        uint256 giftTaxPer10000;
    }

    constructor(Config memory config) {
        uint32 t = uint32(config.timePerDistance) / 4; // the coordinates space is 4 times bigger
        require(t * 4 == config.timePerDistance, "TIME_PER_DIST_NOT_DIVISIBLE_4");

        _genesis = config.genesis;
        _resolveWindow = config.resolveWindow;
        _timePerDistance = t;
        _exitDuration = config.exitDuration;
        _acquireNumSpaceships = config.acquireNumSpaceships;
        _productionSpeedUp = config.productionSpeedUp;
        _frontrunningDelay = config.frontrunningDelay;
        _productionCapAsDuration = config.productionCapAsDuration;
        _upkeepProductionDecreaseRatePer10000th = config.upkeepProductionDecreaseRatePer10000th;
        _fleetSizeFactor6 = config.fleetSizeFactor6;
        _initialSpaceExpansion = config.initialSpaceExpansion;
        _expansionDelta = config.expansionDelta;
        _giftTaxPer10000 = config.giftTaxPer10000;
    }

    // TODO make this facet an initialization contract
    function init() external {
        if (_discovered.minX == 0) {
            _discovered = Discovered({
                minX: _initialSpaceExpansion,
                maxX: _initialSpaceExpansion,
                minY: _initialSpaceExpansion,
                maxY: _initialSpaceExpansion
            });
            emit Initialized(
                _genesis,
                _resolveWindow,
                _timePerDistance,
                _exitDuration,
                _acquireNumSpaceships,
                _productionSpeedUp,
                _frontrunningDelay,
                _productionCapAsDuration,
                _upkeepProductionDecreaseRatePer10000th,
                _fleetSizeFactor6,
                _initialSpaceExpansion,
                _expansionDelta,
                _giftTaxPer10000
            );
        }
    }
}

src/outerspace/facets/OuterSpaceFleetsFa.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.9;

import "./OuterSpaceFacetBase.sol";
import "../interfaces/IOuterSpaceFleets.sol";

contract OuterSpaceFleetsFacet is OuterSpaceFacetBase, IOuterSpaceFleets {
    // solhint-disable-next-line no-empty-blocks
    constructor(Config memory config) OuterSpaceFacetBase(config) {}

    // ---------------------------------------------------------------------------------------------------------------
    // FLEET RESOLUTION, ATTACK / REINFORCEMENT
    // ---------------------------------------------------------------------------------------------------------------

    function resolveFleet(uint256 fleetId, FleetResolution calldata resolution) external {
        require(
            uint256(
                keccak256(
                    abi.encodePacked(
                        keccak256(
                            abi.encodePacked(
                                resolution.secret,
                                resolution.to,
                                resolution.gift,
                                resolution.specific,
                                resolution.arrivalTimeWanted
                            )
                        ),
                        resolution.from,
                        resolution.fleetSender,
                        resolution.operator
                    )
                )
            ) == fleetId,
            "INVALID_FLEET_DATA_OR_SECRET"
        );
        _resolveFleet(fleetId, resolution);
    }

    // ---------------------------------------------------------------------------------------------------------------
    // FLEET SENDING
    // ---------------------------------------------------------------------------------------------------------------

    function send(
        uint256 from,
        uint32 quantity,
        bytes32 toHash
    ) external {
        address sender = _msgSender();
        uint256 fleetId = uint256(keccak256(abi.encodePacked(toHash, from, sender, sender)));
        _unsafe_sendFor(
            fleetId,
            sender,
            FleetLaunch({fleetSender: sender, fleetOwner: sender, from: from, quantity: quantity, toHash: toHash})
        );
    }

    function sendFor(FleetLaunch calldata launch) external {
        //  bytes calldata fleetSignature // TODO for fleetOwner's signature ?

        address operator = _msgSender();
        if (operator != launch.fleetSender) {
            require(_operators[launch.fleetSender][operator], "NOT_AUTHORIZED_TO_SEND");
        }
        uint256 fleetId = uint256(
            keccak256(abi.encodePacked(launch.toHash, launch.from, launch.fleetSender, operator))
        );

        // fleetOwner is basically the one receiving the planet if the attack succeed
        // fleetSender is the one to be used for alliance resolution
        // operator is just so alliance can consider fleetSender based on the rule of that operator
        // if (launch.fleetOwner != launch.fleetSender && launch.fleetOwner != operator) {
        //     // TODO use signature from fleetOwner instead?
        //     require(_operators[launch.fleetOwner][operator], "NOT_AUTHORIZED_TO_FLEET");
        // }

        _unsafe_sendFor(fleetId, operator, launch);
    }

    // ---------------------------------------------------------------------------------------------------------------
    // GETTERS
    // ---------------------------------------------------------------------------------------------------------------

    function getFleet(uint256 fleetId, uint256 from)
        external
        view
        returns (
            address owner,
            uint40 launchTime,
            uint32 quantity,
            uint64 flyingAtLaunch, // can be more than quantity if multiple fleet were launched around the same time from the same planet
            uint64 destroyedAtLaunch
        )
    {
        launchTime = _fleets[fleetId].launchTime;
        quantity = _fleets[fleetId].quantity;
        owner = _fleets[fleetId].owner;

        uint256 timeSlot = launchTime / (_frontrunningDelay / 2);
        destroyedAtLaunch = _inFlight[from][timeSlot].destroyed;
        flyingAtLaunch = _inFlight[from][timeSlot].flying;
    }
}

src/outerspace/facets/OuterSpaceInformat.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.9;

import "./OuterSpaceFacetBase.sol";

contract OuterSpaceInformationFacet is OuterSpaceFacetBase {
    // solhint-disable-next-line no-empty-blocks
    constructor(Config memory config) OuterSpaceFacetBase(config) {}

    function getGeneisHash() external view returns (bytes32) {
        return _genesis;
    }

    function getAllianceRegistry() external view returns (AllianceRegistry) {
        return _allianceRegistry;
    }

    function getDiscovered() external view returns (Discovered memory) {
        return _discovered;
    }

    function getPlanetStates(uint256[] calldata locations)
        external
        view
        returns (ExternalPlanet[] memory planetStates, Discovered memory discovered)
    {
        planetStates = new ExternalPlanet[](locations.length);
        for (uint256 i = 0; i < locations.length; i++) {
            Planet storage planet = _getPlanet(locations[i]);
            (bool active, uint32 numSpaceships) = _activeNumSpaceships(planet.numSpaceships);
            planetStates[i] = ExternalPlanet({
                owner: planet.owner,
                ownershipStartTime: planet.ownershipStartTime,
                exitStartTime: planet.exitStartTime,
                numSpaceships: numSpaceships,
                overflow: planet.overflow,
                lastUpdated: planet.lastUpdated,
                active: active,
                reward: _rewards[locations[i]]
            });
        }
        discovered = _discovered;
    }
}

src/alliances/BasicAlliance.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.9;

import "./AllianceRegistry.sol";
// import "../interfaces/IAlliance.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract BasicAlliance {
    using ECDSA for bytes32;

    bool internal _original;
    AllianceRegistry internal immutable _allianceRegistry;
    address public admin;

    string internal _baseURI;

    mapping(address => uint32) public memberNonces;

    // constructor(AllianceRegistry allianceRegistry, AllianceRegistry.PlayerSubmission[] memory playerSubmissions) {
    //     _allianceRegistry = allianceRegistry;
    //     _allianceRegistry.addMultiplePlayersToAlliance(playerSubmissions);
    // }

    constructor(AllianceRegistry allianceRegistry, string memory baseURI) {
        _allianceRegistry = allianceRegistry;
        _original = true;
        admin = address(1); // lock it
        init(baseURI);
    }

    function init(string memory baseURI) public {
        require(bytes(_baseURI).length == 0, "ALREADY_INITIALISED");
        _baseURI = baseURI;
    }

    function frontendURI() external view returns (string memory) {
        return string(bytes.concat(bytes(_baseURI), bytes(Strings.toHexString(uint256(uint160(address(this))), 20))));
    }

    function setAdminAndAddMembers(address newAdmin, AllianceRegistry.PlayerSubmission[] calldata playerSubmissions)
        public
    {
        address currentAdmin = admin;
        require(currentAdmin == address(0) || msg.sender == currentAdmin, "NOT_ALLOWED");
        admin = newAdmin;
        if (playerSubmissions.length > 0) {
            _allianceRegistry.addMultiplePlayersToAlliance(playerSubmissions);
        }
    }

    function addMembers(AllianceRegistry.PlayerSubmission[] calldata playerSubmissions) external {
        require(msg.sender == admin, "NOT_ALLOWED");
        _allianceRegistry.addMultiplePlayersToAlliance(playerSubmissions);
    }

    function removeMember(address player) external {
        require(msg.sender == admin, "NOT_ALLOWED");
        _allianceRegistry.ejectPlayerFromAlliance(player);
    }

    function claimInvite(
        address player,
        uint32 nonce,
        bytes calldata signature,
        uint32 inviteNonce,
        bytes calldata inviteSignature
    ) external {
        uint256 currentNonce = memberNonces[player];
        require(currentNonce == inviteNonce, "INVALID_NONCE");
        memberNonces[player] = inviteNonce + 1;

        bytes memory message;
        if (inviteNonce == 0) {
            message = abi.encodePacked(
                "\x19Ethereum Signed Message:\n111",
                "Invite Player 0x0000000000000000000000000000000000000000 To Alliance 0x0000000000000000000000000000000000000000"
            );
            _writeUintAsHex(message, 29 + 55, uint160(player));
            _writeUintAsHex(message, 29 + 110, uint160(address(this)));
        } else {
            message = abi.encodePacked(
                "\x19Ethereum Signed Message:\n131",
                "Invite Player 0x0000000000000000000000000000000000000000 To Alliance 0x0000000000000000000000000000000000000000 (nonce:          0)"
            );
            _writeUintAsHex(message, 29 + 55, uint160(player));
            _writeUintAsHex(message, 29 + 110, uint160(address(this)));
            _writeUintAsDecimal(message, 29 + 129, inviteNonce);
        }
        bytes32 digest = keccak256(message);

        address signer = digest.recover(inviteSignature);
        require(admin == signer, "INVALID_INVITE_SIGNATURE");

        _allianceRegistry.addPlayerToAlliance(player, nonce, signature);
    }

    function instantiate(
        address initialAdmin,
        AllianceRegistry.PlayerSubmission[] calldata playerSubmissions,
        bytes32 salt
    ) external {
        require(_original, "CANNOT_INSTANTIATE_FROM_CLONES");
        address newAlliance = Clones.cloneDeterministic(address(this), keccak256(abi.encodePacked(salt, msg.sender)));
        BasicAlliance(newAlliance).init(_baseURI);
        BasicAlliance(newAlliance).setAdminAndAddMembers(initialAdmin, playerSubmissions);
    }

    function getAddress(bytes32 salt) external view returns (address) {
        require(_original, "CANNOT_INSTANTIATE_FROM_CLONES");
        return
            Clones.predictDeterministicAddress(
                address(this),
                keccak256(abi.encodePacked(salt, msg.sender)),
                address(this)
            );
    }

    // function requestToJoin(address player, bytes calldata data) external view returns (bool) {
    //     if (player == _initialMember) {
    //         return true;
    //     } else {
    //         bytes32 digest = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", "Add ", ));
    //         address signer = digest.recover(signature);
    //         require(_outerspace.allianceJoinTime(signer, this), "ONLY_ALLIANCE_MEMBER_CAN_INVITE");
    //     }
    // }

    // function playerHasLeft(address player) external {

    // }

    // TODO library
    bytes internal constant hexAlphabet = "0123456789abcdef";
    bytes internal constant decimalAlphabet = "0123456789";

    function _writeUintAsHex(
        bytes memory data,
        uint256 endPos,
        uint256 num
    ) internal pure {
        while (num != 0) {
            data[endPos--] = bytes1(hexAlphabet[num % 16]);
            num /= 16;
        }
    }

    function _writeUintAsDecimal(
        bytes memory data,
        uint256 endPos,
        uint256 num
    ) internal pure {
        while (num != 0) {
            data[endPos--] = bytes1(decimalAlphabet[num % 10]);
            num /= 10;
        }
    }
}

